///////////////////////////////////////
// FUNCTION: GET INITIAL GRID
///////////////////////////////////////

const getInitialGrid = () => {
  const grid = [];
  for (let row = 0; row < ROWS; row++) {
    const currentRow = [];
    for (let col = 0; col < COLS; col++) {
      currentRow.push(createNode(col, row));
    }
    grid.push(currentRow);
  }
  return grid;
};

///////////////////////////////////////
// FUNCTION: CREATE NODE
///////////////////////////////////////
const createNode = (col : number, row : number) => {
  return {
    col,
    row,
    isStart: row === START_NODE_ROW && col === START_NODE_COL,
    isFinish: row === FINISH_NODE_ROW && col === FINISH_NODE_COL,
    distance: Infinity,
    isVisited: false,
    isWall: false,
    previousNode: null,
  };
};

const getNewGridWithWallToggled = (grid: Array<any>, row : number, col : number) => {
  const newGrid = grid.slice();
  const node = newGrid[row][col];
  const newNode = {
    ...node,
    isWall: !node.isWall,
  };
  newGrid[row][col] = newNode;
  return newGrid;
};

const getNewGridWithStartNode = (grid: Array<any>, row : number, col : number) => {
  const newGrid = grid.slice();

  for (let i = 0; i < ROWS; i++) {
    for (let j = 0; j < COLS; j++) {
      if (newGrid[i][j].isStart) {
        newGrid[i][j].isStart = false;
      }
    }
  }

  const node = newGrid[row][col];
  const newNode = {
    ...node,
    isStart: true,
    isWall: false,
  };

  newGrid[row][col] = newNode;
  return newGrid;
}
